//
//  AutoSaveManager.swift
//  SwiftMedia
//
//  Created by Ojash Neopane on 6/25/25.
//

import Foundation
import Combine

@MainActor
class AutoSaveManager: ObservableObject {
    static let shared = AutoSaveManager()

    @Published var isAutoSaveEnabled = true
    @Published var lastAutoSaveTime: Date?
    @Published var autoSaveError: Error?

    private var saveTimer: Timer?
    private let saveDelay: TimeInterval = 2.0
    private var cancellables = Set<AnyCancellable>()

    // Dependencies
    weak var projectManager: ProjectManager?
    weak var videoPlayerViewModel: VideoPlayerViewModel?
    weak var settingsViewModel: SettingsViewModel?

    private init() {
        setupAutoSavePreference()
    }

    private func setupAutoSavePreference() {
        // Load auto-save preference
        if let savedValue = UserDefaults.standard.object(forKey: "autoSaveEnabled") as? Bool {
            isAutoSaveEnabled = savedValue
        }

        // Save preference when changed
        $isAutoSaveEnabled
            .sink { enabled in
                UserDefaults.standard.set(enabled, forKey: "autoSaveEnabled")
            }
            .store(in: &cancellables)
    }

    // MARK: - Public Methods
    func scheduleSave() {
        guard isAutoSaveEnabled else { return }

        // Cancel existing timer
        saveTimer?.invalidate()

        // Schedule new save
        saveTimer = Timer.scheduledTimer(
            withTimeInterval: saveDelay,
            repeats: false
        ) { [weak self] _ in
            Task { @MainActor [weak self] in
                await self?.performAutoSave()
            }
        }
    }

    func performImmediateSave() async {
        await performAutoSave()
    }

    func cancelPendingSave() {
        saveTimer?.invalidate()
        saveTimer = nil
    }

    // MARK: - Private Methods
    private func performAutoSave() async {
        guard let projectManager = projectManager,
              let viewModel = videoPlayerViewModel,
              let settings = settingsViewModel,
              projectManager.hasUnsavedChanges,
              projectManager.currentProjectURL != nil,
              let player = viewModel.player,
              let videoURL = viewModel.videoURL else {
            return
        }

        do {
            // Measure performance
            let startTime = CFAbsoluteTimeGetCurrent()

            // Perform save
            try await projectManager.saveProject(
                to: projectManager.currentProjectURL,
                markers: viewModel.markers,
                videoURL: videoURL,
                player: player,
                settings: settings
            )

            let saveTime = CFAbsoluteTimeGetCurrent() - startTime

            // Update state
            lastAutoSaveTime = Date()
            autoSaveError = nil

            // Log if save took too long
            if saveTime > 0.1 {
                print("Warning: Auto-save took \(String(format: "%.2f", saveTime * 1000))ms")
            }
        } catch {
            // Don't interrupt user, just log error
            autoSaveError = error
            print("Auto-save failed: \(error.localizedDescription)")
        }
    }

    // MARK: - Change Monitoring
    func startMonitoring(projectManager: ProjectManager,
                        videoPlayerViewModel: VideoPlayerViewModel,
                        settingsViewModel: SettingsViewModel) {
        self.projectManager = projectManager
        self.videoPlayerViewModel = videoPlayerViewModel
        self.settingsViewModel = settingsViewModel

        // Monitor marker changes
        videoPlayerViewModel.$markers
            .dropFirst() // Skip initial value
            .sink { [weak self] _ in
                projectManager.hasUnsavedChanges = true
                self?.scheduleSave()
            }
            .store(in: &cancellables)

        // Monitor settings changes that should trigger save
        Publishers.CombineLatest(
            settingsViewModel.$scrubbingConfigs,
            settingsViewModel.$hasUnsavedChanges
        )
        .dropFirst()
        .filter { _, hasChanges in hasChanges }
        .sink { [weak self] _ in
            projectManager.hasUnsavedChanges = true
            self?.scheduleSave()
        }
        .store(in: &cancellables)
    }

    func stopMonitoring() {
        cancellables.removeAll()
        cancelPendingSave()
        projectManager = nil
        videoPlayerViewModel = nil
        settingsViewModel = nil
    }

    // MARK: - Status
    var autoSaveStatus: String {
        if !isAutoSaveEnabled {
            return "Auto-save disabled"
        } else if let lastSave = lastAutoSaveTime {
            let formatter = RelativeDateTimeFormatter()
            formatter.unitsStyle = .abbreviated
            return "Auto-saved \(formatter.localizedString(for: lastSave, relativeTo: Date()))"
        } else {
            return "Auto-save enabled"
        }
    }
}

