//
//  SeekManager.swift
//  SwiftMedia
//
//  Created by Ojash Neopane on 6/24/25.
//

import Foundation
import AVFoundation

class SeekManager {
    private let seekQueue = DispatchQueue(label: "com.swiftmedia.seekQueue", qos: .userInteractive)
    private var currentSeekID: UUID?
    private let player: AVPlayer
    private var seekStartTime: Date?
    private var completionTimes: [TimeInterval] = []

    init(player: AVPlayer) {
        self.player = player
    }

    func seek(to time: CMTime,
              toleranceBefore: CMTime = .zero,
              toleranceAfter: CMTime = .zero,
              completion: @escaping (Bool) -> Void) {
        let seekID = UUID()
        currentSeekID = seekID
        seekStartTime = Date()

        seekQueue.async { [weak self] in
            guard let self = self, self.currentSeekID == seekID else {
                DispatchQueue.main.async {
                    completion(false)
                }
                return
            }

            DispatchQueue.main.async {
                guard self.currentSeekID == seekID else {
                    completion(false)
                    return
                }

                self.player.seek(to: time,
                               toleranceBefore: toleranceBefore,
                               toleranceAfter: toleranceAfter) { finished in
                    if self.currentSeekID == seekID {
                        if let startTime = self.seekStartTime {
                            let completionTime = Date().timeIntervalSince(startTime)
                            self.recordCompletionTime(completionTime)
                        }
                        completion(finished)
                    } else {
                        completion(false)
                    }
                }
            }
        }
    }

    func cancelPendingSeeks() {
        currentSeekID = nil
    }

    func seekWithDebounce(to time: CMTime,
                         toleranceBefore: CMTime = .zero,
                         toleranceAfter: CMTime = .zero,
                         debounceInterval: TimeInterval = 0.05,
                         completion: @escaping (Bool) -> Void) {
        cancelPendingSeeks()

        seekQueue.asyncAfter(deadline: .now() + debounceInterval) { [weak self] in
            self?.seek(to: time,
                      toleranceBefore: toleranceBefore,
                      toleranceAfter: toleranceAfter,
                      completion: completion)
        }
    }

    private func recordCompletionTime(_ time: TimeInterval) {
        completionTimes.append(time)
        if completionTimes.count > 100 {
            completionTimes.removeFirst()
        }
    }

    var averageSeekTime: TimeInterval {
        guard !completionTimes.isEmpty else { return 0 }
        return completionTimes.reduce(0, +) / Double(completionTimes.count)
    }

    var lastSeekTime: TimeInterval? {
        completionTimes.last
    }

    func performanceReport() -> String {
        let avg = averageSeekTime * 1000
        let last = (lastSeekTime ?? 0) * 1000
        let max = (completionTimes.max() ?? 0) * 1000
        let min = (completionTimes.min() ?? 0) * 1000

        return String(format: "Seek Performance - Avg: %.1fms, Last: %.1fms, Min: %.1fms, Max: %.1fms",
                     avg, last, min, max)
    }
}

//
//  Project.swift
//  SwiftMedia
//
//  Created by Ojash Neopane on 6/25/25.
//

import Foundation
import AppKit

struct Project: Codable {
    static let currentVersion = "1.0"

    let version: String
    var created: Date
    var modified: Date
    var video: VideoReference
    var markers: [Marker]
    var settings: ProjectSettings

    init(version: String = Project.currentVersion,
         created: Date = Date(),
         modified: Date = Date(),
         video: VideoReference,
         markers: [Marker] = [],
         settings: ProjectSettings = ProjectSettings()) {
        self.version = version
        self.created = created
        self.modified = modified
        self.video = video
        self.markers = markers
        self.settings = settings
    }

    struct VideoReference: Codable {
        var absolutePath: String
        var relativePath: String?
        var duration: Double
        var fps: Float
        var resolution: CGSize?
        var fileSize: Int64?
        var lastModified: Date?

        init(absolutePath: String,
             relativePath: String? = nil,
             duration: Double,
             fps: Float,
             resolution: CGSize? = nil,
             fileSize: Int64? = nil,
             lastModified: Date? = nil) {
            self.absolutePath = absolutePath
            self.relativePath = relativePath
            self.duration = duration
            self.fps = fps
            self.resolution = resolution
            self.fileSize = fileSize
            self.lastModified = lastModified
        }
    }

    struct ProjectSettings: Codable {
        var scrubbingConfigs: [ScrubbingConfig]
        var activeScrubbingId: UUID?
        var keyboardShortcuts: KeyboardShortcuts

        init(scrubbingConfigs: [ScrubbingConfig] = ScrubbingConfig.defaultConfigurations(),
             activeScrubbingId: UUID? = nil,
             keyboardShortcuts: KeyboardShortcuts = KeyboardShortcuts()) {
            self.scrubbingConfigs = scrubbingConfigs
            self.activeScrubbingId = activeScrubbingId
            self.keyboardShortcuts = keyboardShortcuts
        }

        struct KeyboardShortcuts: Codable {
            var playPauseKey: String
            var addMarkerKey: String
            var previousMarkerKey: String
            var nextMarkerKey: String
            var deleteMarkerKey: String
            var editMarkerKey: String
            var openSettingsKey: String
            var saveKey: String
            var openKey: String

            init(playPauseKey: String = " ",
                 addMarkerKey: String = "m",
                 previousMarkerKey: String = "[",
                 nextMarkerKey: String = "]",
                 deleteMarkerKey: String = String(NSDeleteCharacter),
                 editMarkerKey: String = "\r",
                 openSettingsKey: String = ",",
                 saveKey: String = "s",
                 openKey: String = "o") {
                self.playPauseKey = playPauseKey
                self.addMarkerKey = addMarkerKey
                self.previousMarkerKey = previousMarkerKey
                self.nextMarkerKey = nextMarkerKey
                self.deleteMarkerKey = deleteMarkerKey
                self.editMarkerKey = editMarkerKey
                self.openSettingsKey = openSettingsKey
                self.saveKey = saveKey
                self.openKey = openKey
            }
        }
    }

    // MARK: - Migration Support
    static func migrate(from oldProject: Project) throws -> Project {
        switch oldProject.version {
        case currentVersion:
            // Current version, no migration needed
            return oldProject
        default:
            // Future migration paths will be added here
            throw MigrationError.unsupportedVersion(oldProject.version)
        }
    }

    // MARK: - Validation
    func validate() throws {
        // Validate version
        guard !version.isEmpty else {
            throw ValidationError.missingVersion
        }

        // Validate video reference
        guard !video.absolutePath.isEmpty else {
            throw ValidationError.missingVideoPath
        }

        // Validate markers don't exceed limit
        guard markers.count <= 60 else {
            throw ValidationError.tooManyMarkers(markers.count)
        }

        // Validate all markers have valid timestamps
        for marker in markers {
            guard marker.timestamp.isValid && marker.timestamp.seconds >= 0 else {
                throw ValidationError.invalidMarkerTimestamp(marker.id)
            }
        }
    }
}

// MARK: - Errors
enum MigrationError: LocalizedError {
    case unsupportedVersion(String)
    case corruptedData

    var errorDescription: String? {
        switch self {
        case .unsupportedVersion(let version):
            return "Cannot migrate from version \(version)"
        case .corruptedData:
            return "Project data is corrupted and cannot be migrated"
        }
    }
}

enum ValidationError: LocalizedError {
    case missingVersion
    case missingVideoPath
    case tooManyMarkers(Int)
    case invalidMarkerTimestamp(UUID)

    var errorDescription: String? {
        switch self {
        case .missingVersion:
            return "Project file is missing version information"
        case .missingVideoPath:
            return "Project is missing video file path"
        case .tooManyMarkers(let count):
            return "Project has too many markers (\(count)). Maximum allowed is 60"
        case .invalidMarkerTimestamp(let id):
            return "Marker \(id) has an invalid timestamp"
        }
    }
}

