//
//  ProjectManager.swift
//  SwiftMedia
//
//  Created by Ojash Neopane on 6/25/25.
//

import Foundation
import AVFoundation
import AppKit

@MainActor
class ProjectManager: ObservableObject {
    static let shared = ProjectManager()

    @Published var currentProject: Project?
    @Published var currentProjectURL: URL?
    @Published var hasUnsavedChanges = false
    @Published var isLoading = false
    @Published var lastError: Error?

    private let fileManager = FileManager.default
    private let backupManager = BackupManager()

    private init() {}

    // MARK: - Save Operations
    func saveProject(
        to url: URL? = nil,
        markers: [Marker],
        videoURL: URL,
        player: AVPlayer,
        settings: SettingsViewModel
    ) async throws {
        let saveURL = try url ?? generateSaveURL(for: videoURL, settings: settings)

        // Create backup before overwriting
        if fileManager.fileExists(atPath: saveURL.path) {
            try await backupManager.createBackup(of: saveURL)
        }

        // Verify disk space (require 2x file size for safety)
        let estimatedSize = Int64(10_000 + (markers.count * 500)) // Rough estimate
        try verifyDiskSpace(required: estimatedSize * 2, at: saveURL)

        // Create project
        let project = try await createProject(
            markers: markers,
            videoURL: videoURL,
            player: player,
            settings: settings
        )

        // Validate before saving
        try project.validate()

        // Perform atomic save
        try await atomicSave(project: project, to: saveURL)

        // Update state
        currentProject = project
        currentProjectURL = saveURL
        hasUnsavedChanges = false
    }

    private func createProject(
        markers: [Marker],
        videoURL: URL,
        player: AVPlayer,
        settings: SettingsViewModel
    ) async throws -> Project {
        guard let asset = player.currentItem?.asset else {
            throw PersistenceError.fileNotFound(videoURL)
        }

        let duration = try await asset.load(.duration)
        let tracks = try await asset.load(.tracks)

        // Get video track for fps and resolution
        let videoTrack = tracks.first { $0.mediaType == .video }
        let fps: Float = 30.0  // Default, will need to update for async loading
        let resolution = videoTrack?.naturalSize

        // Get file attributes
        let attributes = try fileManager.attributesOfItem(atPath: videoURL.path)
        let fileSize = attributes[.size] as? Int64
        let lastModified = attributes[.modificationDate] as? Date

        // Create video reference
        let videoReference = Project.VideoReference(
            absolutePath: videoURL.path,
            relativePath: videoURL.lastPathComponent,
            duration: CMTimeGetSeconds(duration),
            fps: fps,
            resolution: resolution,
            fileSize: fileSize,
            lastModified: lastModified
        )

        // Gather current settings
        let keyboardShortcuts = Project.ProjectSettings.KeyboardShortcuts(
            playPauseKey: settings.playPauseKey,
            addMarkerKey: settings.addMarkerKey,
            previousMarkerKey: settings.previousMarkerKey,
            nextMarkerKey: settings.nextMarkerKey,
            deleteMarkerKey: settings.deleteMarkerKey,
            editMarkerKey: settings.editMarkerKey,
            openSettingsKey: settings.openSettingsKey,
            saveKey: settings.saveKey,
            openKey: settings.openKey
        )

        let projectSettings = Project.ProjectSettings(
            scrubbingConfigs: settings.scrubbingConfigs,
            activeScrubbingId: nil, // Active config tracking will be implemented when multiple configs are active
            keyboardShortcuts: keyboardShortcuts
        )

        return Project(
            created: currentProject?.created ?? Date(),
            modified: Date(),
            video: videoReference,
            markers: markers,
            settings: projectSettings
        )
    }

    private func atomicSave(project: Project, to url: URL) async throws {
        let encoder = JSONEncoder()
        encoder.outputFormatting = [.prettyPrinted, .sortedKeys]
        encoder.dateEncodingStrategy = .iso8601

        let data = try encoder.encode(project)

        // Write to temporary file first
        let tempURL = url.appendingPathExtension("tmp")

        do {
            try data.write(to: tempURL, options: .atomic)

            // Atomic rename
            _ = try fileManager.replaceItem(
                at: url,
                withItemAt: tempURL,
                backupItemName: nil,
                options: [],
                resultingItemURL: nil
            )
        } catch {
            // Cleanup temp file
            try? fileManager.removeItem(at: tempURL)

            // Fallback to direct write
            do {
                try data.write(to: url)
            } catch {
                throw PersistenceError.atomicWriteFailed(url)
            }
        }
    }

    // MARK: - Load Operations
    struct LoadedProjectData {
        let markers: [Marker]
        let videoURL: URL
        let settings: Project.ProjectSettings
    }

    func loadProject(from url: URL) async throws -> LoadedProjectData {
        isLoading = true
        defer { isLoading = false }

        let project = try await loadProjectFile(from: url)

        // Validate and migrate if needed
        let migratedProject = try await validateAndMigrate(project)

        // Resolve video path
        let videoURL = try await resolveVideoPath(migratedProject.video, projectURL: url)

        // Update state
        currentProject = migratedProject
        currentProjectURL = url
        hasUnsavedChanges = false

        return LoadedProjectData(
            markers: migratedProject.markers,
            videoURL: videoURL,
            settings: migratedProject.settings
        )
    }

    private func loadProjectFile(from url: URL) async throws -> Project {
        var data: Data
        var loadedFromBackup = false

        // Try main file first
        do {
            data = try Data(contentsOf: url)
        } catch {
            // Try backup recovery
            if let backupData = try? backupManager.loadBackup(for: url) {
                data = backupData
                loadedFromBackup = true
                print("Loaded from backup due to corrupted main file")
            } else {
                throw PersistenceError.fileNotFound(url)
            }
        }

        let decoder = JSONDecoder()
        decoder.dateDecodingStrategy = .iso8601

        do {
            let project = try decoder.decode(Project.self, from: data)

            // If loaded from backup, save as main file
            if loadedFromBackup {
                try await atomicSave(project: project, to: url)
            }

            return project
        } catch {
            throw PersistenceError.invalidProjectFormat(error)
        }
    }

    private func validateAndMigrate(_ project: Project) async throws -> Project {
        // Validate
        try project.validate()

        // Migrate if needed
        if project.version != Project.currentVersion {
            return try Project.migrate(from: project)
        }

        return project
    }

    // MARK: - Video Path Resolution
    private func resolveVideoPath(_ reference: Project.VideoReference, projectURL: URL) async throws -> URL {
        let strategies: [(String, () -> URL?)] = [
            ("absolute", { URL(fileURLWithPath: reference.absolutePath) }),
            ("relative", {
                guard let relativePath = reference.relativePath else { return nil }
                return projectURL.deletingLastPathComponent().appendingPathComponent(relativePath)
            }),
            ("default directory", {
                guard let defaultDir = UserDefaults.standard.url(forKey: "defaultVideoDirectory"),
                      let filename = URL(fileURLWithPath: reference.absolutePath).lastPathComponent
                        as String? else { return nil }
                return defaultDir.appendingPathComponent(filename)
            })
        ]

        // Try each strategy
        for (name, strategy) in strategies {
            if let url = strategy(), fileManager.fileExists(atPath: url.path) {
                print("Video found using \(name) path: \(url.path)")
                return url
            }
        }

        // If all strategies fail, prompt user
        throw VideoResolutionError.videoNotFound(
            attemptedPaths: strategies.compactMap { $0.1()?.path }
        )
    }

    // MARK: - Helper Methods
    private func generateSaveURL(for videoURL: URL, settings: SettingsViewModel) throws -> URL {
        let metadataDir: URL

        if !settings.metadataDirectory.isEmpty,
           let url = URL(string: settings.metadataDirectory) {
            metadataDir = url
        } else {
            // Use video directory
            metadataDir = videoURL.deletingLastPathComponent()
        }

        // Ensure directory exists
        try fileManager.createDirectory(at: metadataDir, withIntermediateDirectories: true)

        // Generate filename
        let videoName = videoURL.deletingPathExtension().lastPathComponent
        let projectName = "\(videoName).swiftmedia"

        return metadataDir.appendingPathComponent(projectName)
    }

    private func verifyDiskSpace(required: Int64, at url: URL) throws {
        let resourceValues = try url.resourceValues(forKeys: [.volumeAvailableCapacityKey])

        if let available = resourceValues.volumeAvailableCapacity,
           Int64(available) < required {
            throw PersistenceError.diskSpaceInsufficient(
                required: required,
                available: Int64(available)
            )
        }
    }

    // MARK: - Project File Extension
    static let projectFileExtension = "swiftmedia"

    static var projectFileTypes: [String] {
        [projectFileExtension]
    }
}

// MARK: - Backup Manager
private class BackupManager {
    private let maxBackupAge: TimeInterval = 7 * 24 * 60 * 60 // 7 days

    func createBackup(of url: URL) async throws {
        let backupURL = url.appendingPathExtension("backup")

        // Remove old backup
        try? FileManager.default.removeItem(at: backupURL)

        // Create new backup
        do {
            try FileManager.default.copyItem(at: url, to: backupURL)
        } catch {
            throw PersistenceError.backupCreationFailed(url, underlyingError: error)
        }
    }

    func loadBackup(for url: URL) throws -> Data {
        let backupURL = url.appendingPathExtension("backup")
        return try Data(contentsOf: backupURL)
    }

    func cleanOldBackups(at directory: URL) async {
        let enumerator = FileManager.default.enumerator(
            at: directory,
            includingPropertiesForKeys: [.creationDateKey],
            options: [.skipsHiddenFiles]
        )

        while let url = enumerator?.nextObject() as? URL {
            guard url.pathExtension == "backup" else { continue }

            if let attributes = try? FileManager.default.attributesOfItem(atPath: url.path),
               let creationDate = attributes[.creationDate] as? Date,
               Date().timeIntervalSince(creationDate) > maxBackupAge {
                try? FileManager.default.removeItem(at: url)
            }
        }
    }
}
