        In CI or when MinIO is not available, these tests will be skipped.
        """
        import boto3
        from botocore.exceptions import ClientError

        # Test if MinIO is available
        try:
            client = boto3.client(
                "s3",
                endpoint_url="http://localhost:9000",
                aws_access_key_id="minioadmin",
                aws_secret_access_key="minioadmin",
            )
            # Try to create bucket if it doesn't exist
            try:
                client.create_bucket(Bucket="e-vals-test")
            except ClientError as e:
                if e.response["Error"]["Code"] != "BucketAlreadyOwnedByYou":
                    raise

            # Clear any existing test data
            paginator = client.get_paginator("list_objects_v2")
            pages = paginator.paginate(Bucket="e-vals-test")

            delete_keys = []
            for page in pages:
                if "Contents" in page:
                    delete_keys.extend(
                        [{"Key": obj["Key"]} for obj in page["Contents"]]
                    )

            if delete_keys:
                client.delete_objects(
                    Bucket="e-vals-test", Delete={"Objects": delete_keys}
                )

        except Exception:
            pytest.skip("MinIO not available - skipping S3 integration tests")

        # Create storage instance
        storage = S3Storage(
            bucket_name="e-vals-test",
            endpoint_url="http://localhost:9000",
            access_key_id="minioadmin",
            secret_access_key="minioadmin",
        )

        yield storage

        # Cleanup after tests
        try:
            delete_keys = []
            async for key in storage.list_keys():
                delete_keys.append({"Key": key})

            if delete_keys:
                client.delete_objects(
                    Bucket="e-vals-test", Delete={"Objects": delete_keys}
                )
        except Exception:
