"""FastAPI router for collector endpoints."""

from datetime import datetime
from typing import Dict, Any

from fastapi import APIRouter, Depends, HTTPException, status
from sqlalchemy.ext.asyncio import AsyncSession

from src.db.session import get_db
from src.collector.schemas import (
    PolicyCreate, PolicyResponse,
    TrajectoryCreate, TrajectoryResponse,
    HealthResponse, ErrorResponse
)
from src.collector.service import CollectorService


router = APIRouter(prefix="/collector", tags=["collector"])


def get_collector_service(db: AsyncSession = Depends(get_db)) -> CollectorService:
    """Dependency to get collector service instance."""
    return CollectorService(db)


@router.post(
    "/policy",
    response_model=PolicyResponse,
    status_code=status.HTTP_201_CREATED,
    responses={
        400: {"model": ErrorResponse, "description": "Invalid policy data"},
        409: {"model": ErrorResponse, "description": "Policy already exists"}
    }
)
async def register_policy(
    policy: PolicyCreate,
    service: CollectorService = Depends(get_collector_service)
) -> PolicyResponse:
    """Register a new policy configuration.

    Creates a new policy that can be used to generate trajectories.
    Policy names must be unique within the system.
    """
    try:
        created_policy = await service.create_policy(policy)
        return PolicyResponse.model_validate(created_policy)
    except ValueError as e:
        raise HTTPException(
            status_code=status.HTTP_409_CONFLICT,
            detail={"error": "conflict", "message": str(e)}
        )
    except Exception as e:
        raise HTTPException(
            status_code=status.HTTP_400_BAD_REQUEST,
            detail={"error": "bad_request", "message": str(e)}
        )


@router.post(
    "/trajectory",
    response_model=TrajectoryResponse,
    status_code=status.HTTP_201_CREATED,
    responses={
        400: {"model": ErrorResponse, "description": "Invalid trajectory data"},
        404: {"model": ErrorResponse, "description": "Policy or query not found"}
    }
)
async def log_trajectory(
    trajectory: TrajectoryCreate,
    service: CollectorService = Depends(get_collector_service)
) -> TrajectoryResponse:
    """Log a new trajectory (model response).

    Important: This endpoint does NOT accept reward data.
    Rewards must be added separately through the reward endpoints.
    """
    try:
        created_trajectory = await service.create_trajectory(trajectory)
        return TrajectoryResponse.model_validate(created_trajectory)
    except ValueError as e:
        if "not found" in str(e):
            raise HTTPException(
                status_code=status.HTTP_404_NOT_FOUND,
                detail={"error": "not_found", "message": str(e)}
            )
        raise HTTPException(
            status_code=status.HTTP_400_BAD_REQUEST,
            detail={"error": "bad_request", "message": str(e)}
        )
    except Exception as e:
        raise HTTPException(
            status_code=status.HTTP_500_INTERNAL_SERVER_ERROR,
            detail={"error": "internal_error", "message": str(e)}
        )


@router.get(
    "/health",
    response_model=HealthResponse,
    responses={
        503: {"model": ErrorResponse, "description": "Service unhealthy"}
    }
)
async def health_check(
    service: CollectorService = Depends(get_collector_service)
) -> HealthResponse:
    """Check collector service health.

    Verifies database and storage backend connectivity.
    """
    try:
        health_status = await service.check_health()

        # Determine overall health
        is_healthy = all(
            status == "healthy" 
            for status in health_status.values()
        )

        response = HealthResponse(
            status="healthy" if is_healthy else "degraded",
            version="0.1.0",
            database=health_status["database"],
            storage=health_status["storage"],
            timestamp=datetime.utcnow()
        )

        if not is_healthy:
            raise HTTPException(
                status_code=status.HTTP_503_SERVICE_UNAVAILABLE,
                detail={
                    "error": "service_unavailable",
                    "message": "One or more components are unhealthy",
                    "details": health_status
                }
            )

        return response

    except HTTPException:
        raise
    except Exception as e:
        raise HTTPException(
            status_code=status.HTTP_503_SERVICE_UNAVAILABLE,
            detail={
                "error": "service_unavailable",
                "message": "Health check failed",
                "details": {"error": str(e)}
            }
        )

